import React, { useState } from 'react';
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';

const Streak = ({ streakName, onPress, active }) => {

    const [count = 1, setCount] = useState(0);
    const containerStyle = [
        styles.allStreakContainer,
        active && styles.activeStreakContainer,
    ];
    const handlePress = () => {
        setCount(count + 1);
        onPress();
    };

    return (
        <TouchableOpacity onPress={handlePress}>
            <View style={containerStyle}>
                <Text>{ streakName }</Text>
                {active && <Text>Count: {count}</Text>}
            </View>
        </TouchableOpacity>
    )
}

export default Streak;

const styles = StyleSheet.create({
    allStreakContainer: {
        width: '100%',
        height: 70,
        backgroundColor: 'red',
        borderRadius: 5,
        marginRight: 10,
        marginTop: 10,
        alignItems: 'center',
    },
    activeStreakContainer: {
        backgroundColor: 'red',
        height: 150,
        width: 150,
    }
})
