import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, TouchableOpacity, View, Alert } from 'react-native';
import CircularProgress from 'react-native-circular-progress-indicator';

const Streak = ({ streakName, onPress, active, createdDate }) => {
  const [count, setCount] = useState(1);

  const containerStyle = [
    styles.streakContainer,
    active ? styles.activeStreakContainer : styles.inactiveStreakContainer,
  ];

  const textStyle = active ? styles.activeStreakText : styles.inactiveStreakText;

  const handlePress = () => {
    if (active) {
      const currentDate = new Date().setHours(0, 0, 0, 0);
      const streakStartDate = new Date(createdDate).setHours(0, 0, 0, 0);
      if (currentDate > streakStartDate) {
        Alert.alert(
          'Confirm Streak',
          'Are you ready to add to your streak?',
          [
            { text: 'Cancel', style: 'cancel' },
            { text: 'Confirm', onPress: incrementCount },
          ]
        );
      } else {
        Alert.alert('Cannot Streak', 'You can only start streaking after the start of a new day.');
      }
    } else {
      incrementCount();
    }
  };

  const incrementCount = () => {
    const newCount = count + 1;
    setCount(newCount);
    onPress();
  };

  const formattedDate = new Date(createdDate).toLocaleDateString('en-US', {
    month: 'short',
    day: 'numeric',
    year: 'numeric',
  });

  return (
    <TouchableOpacity onPress={handlePress} activeOpacity={0.5}>
      <View style={containerStyle}>
        <Text style={textStyle} numberOfLines={1}>{streakName}</Text>
        {active && (
          <CircularProgress
            value={count}
            maxValue={10}
            progressValueColor="#383D54"
            progressValueFontSize={30}
            radius={45}
            strokeColorConfig={[
              { color: '#3DC330', value: 0 },
              { color: '#6627B8', value: 25 },
              { color: '#279AB8', value: 50 },
              { color: '#D8E867', value: 75 },
              { color: '#FF2200', value: 100 },
            ]}
            activeStrokeWidth={15}
            inActiveStrokeWidth={15}
            duration={500}
          />
        )}
        {!active && (
          <View>
            <Text style={styles.info}>Last Streaked: {count}</Text>
            <Text style={styles.info}>Date Added: {formattedDate}</Text>
          </View>
        )}
      </View>
    </TouchableOpacity>
  );
};

export default Streak;

const styles = StyleSheet.create({
  streakContainer: {
    width: '100%',
    height: 70,
    backgroundColor: 'white',
    borderRadius: 5,
    marginRight: 10,
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  inactiveStreakContainer: {
    alignItems: 'flex-start',
    justifyContent: 'space-evenly',
    paddingHorizontal: 10,
    marginBottom: 10,
  },
  activeStreakContainer: {
    backgroundColor: 'white',
    height: 150,
    width: 150,
    paddingTop: 15,
    paddingBottom: 15,
  },
  inactiveStreakText: {
    fontSize: 18,
    fontWeight: '300',
  },
  activeStreakText: {
    fontSize: 18,
    fontWeight: '300',
  },
  info: {
    fontSize: 12,
    fontWeight: '400',
    color: '#909090',
  },
});
