import React, { useState } from 'react';
import { Text, StyleSheet, View, SafeAreaView, StatusBar, Image, ScrollView, TouchableOpacity, Modal, TextInput } from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import Streak from '../components/Streak';

const Home = () => {

    const [showPopup, setShowPopup] = useState(false);
    const [streakName, setStreakName] = useState('');
    const [newStreak, setNewStreak] = useState([]);

    const openPopup = () => {
        setShowPopup(true);
    };

    const closePopup = () => {
        setShowPopup(false);
    };

    const createStreak = (text) => {
        setStreakName(text);
    };

    const handleCreateStreak = () => {
        console.log('Streak Name: ', streakName);
        setNewStreak([...newStreak, streakName]);
        setStreakName('');
        closePopup();
    };

    return (
        <View style={styles.container}>

            <StatusBar barStyle="dark-content" />
            <View style={styles.header}>
                    <Image source={require('../assets/Logo.png')} style={styles.logo} />
                    <TouchableOpacity 
                        onPress={openPopup} 
                        style={{
                            position: 'absolute',
                            right: 25,
                            bottom: 20,
                            alignItems: 'center',
                            justifyContent: 'center',
                    }}>
                        <AntDesign name='pluscircleo' size={28} />
                    </TouchableOpacity>
            </View>



            <SafeAreaView style={styles.content}>    
                <View style={styles.activeStreaksContainer}>
                    <Text>Active Streaks</Text>
                    <ScrollView horizontal style={styles.activeStreaks}>

                    </ScrollView>
                </View>
                <View style={styles.allStreaksContainer}>
                    <Text>All Streaks</Text>
                    <ScrollView vertical style={styles.allStreaks}>
                        {newStreak.map((streak, index) => (
                            <Streak key={index} streakName={streak} />
                        ))}
                    </ScrollView>
                </View>
            </SafeAreaView>



            <Modal visible={showPopup} animationType="fade" transparent>
                <View style={styles.modalContainer}>
                    <View style={styles.popupContainer}>
                        <Text>Add a New Streek</Text>
                        <TextInput 
                            style={styles.inputField}
                            placeholder="Streak Name"
                            placeholderTextColor={'gray'}
                            value={streakName}
                            onChangeText={createStreak}
                        />
                        <TouchableOpacity 
                            onPress={handleCreateStreak} 
                            style={[styles.createButton, streakName.trim() === '' && { opacity: 0.5 }]}
                            disabled={streakName.trim() === ''}
                        >
                            <Text>Done</Text>
                        </TouchableOpacity>
                        <TouchableOpacity onPress={closePopup}>
                            <Text>Cancel</Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </Modal>

        </View>
    );
};

export default Home;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#76DBD1',
        alignItems: 'center',
        justifyContent: 'center',
    },
    header: {
        height: 120,
        width: '100%',
        borderBottomLeftRadius: 15,
        borderBottomRightRadius: 15,
        backgroundColor: '#E8E8E8',
        alignItems: 'center',
        justifyContent: 'flex-end',
        paddingBottom: 20,

    },
    logo: {
        height: 30,
        width: 125,
        right: 2,
        resizeMode: 'contain',
    },
    content: {
        flex: 1,
        backgroundColor: '#76DBD1',
        alignItems: 'center',
        justifyContent: 'space-evenly',
        width: '100%',
    },
    activeStreaksContainer: {
        height: 350,
        width: '100%',
        backgroundColor: '#E8E8E8',
        padding: 20,
    },
    allStreaksContainer: {
        height: 350,
        width: '100%',
        backgroundColor: '#E8E8E8',
        padding: 20,
        alignContent: 'center',
    },
    modalContainer: {
        flex: 1,
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
        alignItems: 'center',
        justifyContent: 'flex-start',
    },
    popupContainer: {
        backgroundColor: '#FFF',
        padding: 20,
        borderRadius: 10,
        alignItems: 'center',
        width: '80%',
        height: '50%',
        top: '15%',
    },
    inputField: {
        height: 40,
        width: '100%',
        borderWidth: 1,
        borderColor: 'gray',
        borderRadius: 5,
        marginBottom: 10,
        paddingHorizontal: 10,
    },
    createButton: {
        backgroundColor: '#76DBD1',
        paddingHorizontal: 20,
        paddingVertical: 10,
        borderRadius: 5,
        marginBottom: 10,
    },
});
