import React, { useState } from 'react';
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';

const Streak = ({ streakName, onPress, active }) => {

    const containerStyle = [
        styles.allStreakContainer,
        active && styles.activeStreakContainer,
    ];

    return (
        <TouchableOpacity onPress={onPress}>
            <View style={containerStyle}>
                <Text>{ streakName }</Text>
            </View>
        </TouchableOpacity>
    )
}

export default Streak;

const styles = StyleSheet.create({
    allStreakContainer: {
        width: '100%',
        height: 70,
        backgroundColor: 'red',
        borderRadius: 5,
        marginRight: 10,
        marginTop: 10,
        alignItems: 'center',
    },
    activeStreakContainer: {
        backgroundColor: 'red',
        height: 70,
        width: '100%',
    }
})
