import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, TouchableOpacity, View, Alert } from 'react-native';
import CircularProgress from 'react-native-circular-progress-indicator';

const Streak = ({ streakName, onPress, active, createdDate, frequency }) => {
  const [count, setCount] = useState(1);
  const [canStreak, setCanStreak] = useState(false);

  const containerStyle = [
    styles.streakContainer,
    active ? styles.activeStreakContainer : styles.inactiveStreakContainer,
  ];

  const textStyle = active ? styles.activeStreakText : styles.inactiveStreakText;

  const handlePress = () => {
    if (active) {
      if (canStreak) {
        setCanStreak(false);
        Alert.alert(
          'Confirm Streak',
          'Are you ready to add to your streek?',
          [
            { text: 'Cancel', style: 'cancel' },
            { text: 'Confirm', onPress: incrementCount },
          ]
        );
      } else {
        let interval;

        if (frequency === 'Hourly') { interval = 'hour'; }
        else if (frequency === 'Daily') { interval = 'day'; }
        else if (frequency === 'Weekly') { interval = 'week'; }
        else if (frequency === 'Monthly') { interval = 'month'; }

        Alert.alert('Cannot Streek', 'You can only add to this streek once every ' + interval + '');
      }
    } else {
      incrementCount();
    }
  };

  const incrementCount = () => {
    const newCount = count + 1;
    setCount(newCount);
    onPress(count); // Pass the count value to the parent component via the onPress function
  };

  useEffect(() => {
    let interval;
    const today = new Date().setHours(0, 0, 0, 0);

    if (frequency === 'Hourly') {
      interval = 60 * 60 * 1000;
    } else if (frequency === 'Daily') {
      interval = 24 * 60 * 60 * 1000; 
    } else if (frequency === 'Weekly') {
      interval = 7 * 24 * 60 * 60 * 1000;
    } else if (frequency === 'Monthly') {
      const currentMonth = new Date().getMonth();
      const nextMonth = currentMonth === 11 ? 0 : currentMonth + 1;
      const nextMonthDate = new Date();
      nextMonthDate.setMonth(nextMonth);
      nextMonthDate.setDate(1);
      nextMonthDate.setHours(0, 0, 0, 0);
      const timeUntilNextMonth = nextMonthDate - today;

      interval = timeUntilNextMonth;
    }

    const timer = setTimeout(() => {
      setCanStreak(true);
    }, interval);

    return () => clearTimeout(timer);
  }, [frequency]);  

  const formattedDate = new Date(createdDate).toLocaleDateString('en-US', {
    month: 'short',
    day: 'numeric',
    year: 'numeric',
  });

  return (
    <TouchableOpacity onPress={handlePress} activeOpacity={0.5}>
      <View style={containerStyle}>
        <Text style={textStyle} numberOfLines={1}>{streakName}</Text>
        {active && (
          <CircularProgress
            value={count}
            maxValue={10}
            progressValueColor="#383D54"
            progressValueFontSize={30}
            radius={45}
            strokeColorConfig={[
              { color: '#3DC330', value: 0 },
              { color: '#6627B8', value: 25 },
              { color: '#279AB8', value: 50 },
              { color: '#D8E867', value: 75 },
              { color: '#FF2200', value: 100 },
            ]}
            activeStrokeWidth={15}
            inActiveStrokeWidth={15}
            duration={500}
          />
        )}
        {!active && (
          <View>
            <Text style={styles.info}>Last Streaked: {count}</Text>
            <Text style={styles.info}>Date Added: {formattedDate}</Text>
          </View>
        )}
      </View>
    </TouchableOpacity>
  );
};

export default Streak;

const styles = StyleSheet.create({
  streakContainer: {
    width: '100%',
    height: 70,
    backgroundColor: 'white',
    borderRadius: 5,
    marginRight: 10,
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  inactiveStreakContainer: {
    alignItems: 'flex-start',
    justifyContent: 'space-evenly',
    paddingHorizontal: 10,
    marginBottom: 10,
  },
  activeStreakContainer: {
    backgroundColor: 'white',
    height: 150,
    width: 150,
    paddingTop: 15,
    paddingBottom: 15,
  },
  inactiveStreakText: {
    fontSize: 18,
    fontWeight: '300',
  },
  activeStreakText: {
    fontSize: 18,
    fontWeight: '300',
  },
  info: {
    fontSize: 12,
    fontWeight: '400',
    color: '#909090',
  },
});
