import React, { useState } from 'react';
import { Text, StyleSheet, View, SafeAreaView, StatusBar, Image, ScrollView, TouchableOpacity } from 'react-native';
import { AntDesign } from '@expo/vector-icons';

const Home = () => {

    const [showPopup, setShowPopup] = useState(false);

    const togglePopup = () => {
        setShowPopup(!showPopup);
    }

    return (
        <View style={styles.container}>
            <StatusBar barStyle="dark-content" />
            <View style={styles.header}>
                    <Image source={require('../assets/Logo.png')} style={styles.logo} />
                    <TouchableOpacity onPress={togglePopup} style={{
                        position: 'absolute',
                        right: 0,
                        bottom: 0,
                        height: 60,
                        width: 60,
                        backgroundColor: '#76DBD1',
                        borderRadius: 30,
                        alignItems: 'center',
                        justifyContent: 'center',
                        shadowColor: '#000',
                    }}>
                        <AntDesign 
                            name='pluscircleo' 
                            size={28} 
                            style={{ 
                                position: 'absolute', 
                                right: 25, 
                                bottom: 20, 
                            }} 
                        />
                    </TouchableOpacity>
            </View>
            <SafeAreaView style={styles.content}>    
                <View style={styles.activeStreaksContainer}>
                    <Text>Active Streaks</Text>
                    <ScrollView horizontal style={styles.activeStreaks}>
                    </ScrollView>
                </View>
            </SafeAreaView>
        </View>
    );
};

export default Home;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#76DBD1',
        alignItems: 'center',
        justifyContent: 'center',
    },
    header: {
        height: 120,
        width: '100%',
        borderBottomLeftRadius: 15,
        borderBottomRightRadius: 15,
        backgroundColor: '#E8E8E8',
        alignItems: 'center',
        justifyContent: 'flex-end',
        paddingBottom: 20,

    },
    content: {
        flex: 1,
        backgroundColor: '#76DBD1',
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',
    },
    logo: {
        height: 30,
        width: 125,
        right: 2,
        resizeMode: 'contain',
    },
    activeStreaksContainer: {
        height: 200,
        width: '100%',
        backgroundColor: '#E8E8E8',
        padding: 20,
    },
});
