import React, { useState } from 'react';
import { Text, StyleSheet, View, SafeAreaView, StatusBar, Image, ScrollView, TouchableOpacity, Modal } from 'react-native';
import { AntDesign } from '@expo/vector-icons';

const Home = () => {

    const [showPopup, setShowPopup] = useState(false);

    const openPopup = () => {
        setShowPopup(true);
    };

    const closePopup = () => {
        setShowPopup(false);
    };

    return (
        <View style={styles.container}>

            <StatusBar barStyle="dark-content" />
            <View style={styles.header}>
                    <Image source={require('../assets/Logo.png')} style={styles.logo} />
                    <TouchableOpacity 
                        onPress={openPopup} 
                        style={{
                            position: 'absolute',
                            right: 25,
                            bottom: 20,
                            alignItems: 'center',
                            justifyContent: 'center',
                    }}>
                        <AntDesign name='pluscircleo' size={28} />
                    </TouchableOpacity>
            </View>



            <SafeAreaView style={styles.content}>    
                <View style={styles.activeStreaksContainer}>
                    <Text>Active Streaks</Text>
                    <ScrollView horizontal style={styles.activeStreaks}>
                    </ScrollView>
                </View>
            </SafeAreaView>



            <Modal visible={showPopup} animationType="slide" transparent style={styles.modalContainer}>
                <View style={styles.popupContainer}>
                    <View style={styles.popupContent}>
                        {/* Add your popup content here */}
                        <Text>This is a popup</Text>
                        <TouchableOpacity onPress={closePopup} style={styles.closeButton}>
                            <AntDesign name="closecircleo" size={24} />
                        </TouchableOpacity>
                    </View>
                </View>
            </Modal>

        </View>
    );
};

export default Home;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#76DBD1',
        alignItems: 'center',
        justifyContent: 'center',
    },
    header: {
        height: 120,
        width: '100%',
        borderBottomLeftRadius: 15,
        borderBottomRightRadius: 15,
        backgroundColor: '#E8E8E8',
        alignItems: 'center',
        justifyContent: 'flex-end',
        paddingBottom: 20,

    },
    content: {
        flex: 1,
        backgroundColor: '#76DBD1',
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',
    },
    logo: {
        height: 30,
        width: 125,
        right: 2,
        resizeMode: 'contain',
    },
    activeStreaksContainer: {
        height: 200,
        width: '100%',
        backgroundColor: '#E8E8E8',
        padding: 20,
    },
    modalContainer: {
        flex: 1,
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
        alignItems: 'center',
        justifyContent: 'center',
    },
    popupContainer: {
        backgroundColor: '#FFF',
        padding: 20,
        borderRadius: 10,
        alignItems: 'center',
        justifyContent: 'center',
    },
});
