import React, { useEffect } from 'react';
import { Text, StyleSheet, View, SafeAreaView, StatusBar, Image } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

const SplashScreen = ({ navigation }) => {
    useEffect(() => {
      // Simulate a delay for the splash screen
      const timer = setTimeout(() => {
        // Navigate to the home screen after the delay
        navigation.replace('Home');
      }, 200000000000000000); // Adjust the delay duration as needed

      // Clear the timer if the component unmounts
      return () => clearTimeout(timer);
    }, []);

    return (
        <LinearGradient colors={['#FBC02D', '#FF4081']} style={styles.container}>
        <StatusBar translucent backgroundColor="transparent" />
        <View style={styles.content}>
          <Text style={styles.text}>Splash Screen</Text>
        </View>
      </LinearGradient>
    );
  };

export default SplashScreen;

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    content: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    text: {
        fontSize: 24,
        fontWeight: 'bold',
        color: '#FFF',
    },
});
