import React from 'react';
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';

const Streak = ({ streakName, onPress, active }) => {
  const containerStyle = [
    styles.streakContainer,
    active && styles.streakContainerActive,
  ];

  return (
    <TouchableOpacity onPress={onPress}>
      <View style={containerStyle}>
        <Text>{streakName}</Text>
      </View>
    </TouchableOpacity>
  );
};

export default Streak;

const styles = StyleSheet.create({
  streakContainer: {
    width: '100%',
    height: 70,
    backgroundColor: 'red',
    borderRadius: 5,
    marginRight: 10,
    marginTop: 10,
    alignItems: 'center',
  },
  streakContainerActive: {
    borderRadius: 50, // Example of changing the shape for active streaks
    backgroundColor: 'blue', // Example of changing the background color for active streaks
  },
});
